import React, { useEffect, useState } from 'react';
import './App.css';
import Post from './Posts';

import { createClient } from '@supabase/supabase-js'




function App() {
  // Estado para almacenar las publicaciones
  const [posts, setPosts] = useState([]);
  // Create a single supabase client for interacting with your database
  const supabase = createClient('https://lmkknxrqzmtwiyulujei.supabase.co', 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6Imxta2tueHJxem10d2l5dWx1amVpIiwicm9sZSI6ImFub24iLCJpYXQiOjE3MzA5Njg5OTksImV4cCI6MjA0NjU0NDk5OX0.Z8T1Cu2YVhfQFCsxfCDdJkmaI2FRp4fGVWt3tdXl9gU')

  // Llamada a la API al cargar el componente
useEffect(() => {
  fetch('https://lmkknxrqzmtwiyulujei.supabase.co', {
    headers: {
      'apikey': process.env.REACT_APP_SUPABASE_API_KEY, // Añade tu API Key aquí
      'Authorization': `Bearer ${process.env.REACT_APP_SUPABASE_API_KEY}`
    }
  })
    .then(response => response.json())
    .then(data => setPosts(data))
    .catch(error => console.error('Error al obtener los datos:', error));
}, []);


  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h1>Publicaciones</h1>
      {posts.length > 0 ? (
        <ul>
          {posts.map((post) => (
            <Post key={post.id} title={post.title} body={post.body} postId={post.id} />
          ))}
        </ul>
      ) : (
        <p>Cargando publicaciones...</p>
      )}
    </div>
  );
}

export default App;
