import React, { useEffect, useState } from 'react';
import './App.css';
import Post from './Posts';

function App() {
  // Estado para almacenar las publicaciones
  const [posts, setPosts] = useState([]);

  // Llamada a la API al cargar el componente
  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await fetch('https://lmkknxrqzmtwiyulujei.supabase.co', {
          headers: {
            'apikey': process.env.REACT_APP_SUPABASE_API_KEY,
            'Authorization': `Bearer ${process.env.REACT_APP_SUPABASE_API_KEY}`
          }
        });

        const data = await response.json();
        setPosts(data);
      } catch (error) {
        console.error('Error al obtener los datos:', error);
      }
    };

    fetchPosts();
  }, []);

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h1>Publicaciones</h1>
      {posts.length > 0 ? (
        <ul>
          {posts.map((post) => (
            <Post key={post.id} title={post.title} body={post.body} postId={post.id} />
          ))}
        </ul>
      ) : (
        <p>Cargando publicaciones...</p>
      )}
    </div>
  );
}

export default App;
