import React, { useEffect } from 'react';
import { testConnection } from './SupabaseClient.ts'// carpeta superior a index.tsx


export default function App() {
  useEffect(() => {
    testConnection(); // Llama a la prueba de conexi칩n al cargar la app
  }, []);

  /* opcional, solo necesitamos la llamada a `testConnection`: */
  return (
    <div
      style={{
        maxWidth: "500px",
        margin: "20px auto",
        fontFamily: "Arial, sans-serif",
        textAlign: "center",
        padding: "20px",
        boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
        borderRadius: "10px",
        backgroundColor: "#f9f9f9",
      }}
    >
      <h2 style={{ color: "#333" }}>Task Manager</h2>

      {/* Input para a침adir tareas */}
      <form id="add-task-form" style={{ marginBottom: "20px" }}>
        <input
          type="text"
          id="task-input"
          placeholder="Enter a new task..."
          style={{
            width: "80%",
            padding: "10px",
            fontSize: "14px",
            border: "1px solid #ddd",
            borderRadius: "5px",
            marginRight: "5px",
          }}
          required
        />
        <button
          type="submit"
          style={{
            padding: "10px 20px",
            fontSize: "14px",
            color: "white",
            backgroundColor: "#4caf50",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          Add Task
        </button>
      </form>

      {/* Lista de tareas */}
      <ul id="task-list" style={{ listStyle: "none", padding: "0" }}>
        {/* Ejemplo de tarea */}
        <li
          style={{
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            marginBottom: "10px",
            padding: "10px",
            border: "1px solid #ddd",
            borderRadius: "5px",
            backgroundColor: "#fff",
          }}
        >
          <span style={{ flex: 1, textAlign: "left" }}>Sample Task</span>
          <button
            className="delete-task"
            style={{
              padding: "5px 10px",
              fontSize: "12px",
              color: "white",
              backgroundColor: "#f44336",
              border: "none",
              borderRadius: "5px",
              cursor: "pointer",
            }}
          >
            Delete
          </button>
        </li>
      </ul>
    </div>
  );
}
