import React, { useEffect } from 'react';
import { testConnection } from '../supabaseClient'; // carpeta superior a index.tsx


function App() {
  const [data, setData] = useState([]);  // Estado para guardar los datos
  const [loading, setLoading] = useState(true);  // Estado para el indicador de carga
  const [newPost, setNewPost] = useState({ title: '', body: '' });  // Estado para el formulario
  const [editingPost, setEditingPost] = useState(null);  // Estado para saber qué post estás editando

  // Función para obtener los datos de Supabase
  const fetchData = async () => {
    const { data, error } = await supabase
      .from('prueba')  // Nombre de la tabla
      .select('*');  // Seleccionar todos los campos

    if (error) {
      console.error('Error fetching data:', error.message);
    } else {
      setData(data);  // Guardar los datos en el estado
    }
    setLoading(false);  // Cambiar el estado de carga cuando se obtiene la respuesta
  };

  // Función para insertar un nuevo post
  const insertData = async (e) => {
    e.preventDefault(); // Evitar que la página se recargue
    const { title, body } = newPost;

    if (!title || !body) {
      alert('Por favor, llena todos los campos');
      return;
    }

    const { data, error } = await supabase
      .from('prueba')  // Nombre de la tabla
      .insert([
        { title, body },
      ]);

    if (error) {
      console.error('Error inserting data:', error.message);
    } else {
      console.log('Data inserted successfully:', data);
      setNewPost({ title: '', body: '' });  // Limpiar el formulario
      fetchData();  // Volver a cargar los datos
    }
  };

  // Función para editar un post
  const editPost = (post) => {
    setEditingPost(post);  // Establecer el post que estamos editando
    setNewPost({ title: post.title, body: post.body });  // Rellenar el formulario con los datos del post
  };

  // Función para actualizar un post
  const updatePost = async (e) => {
    e.preventDefault();
    const { title, body } = newPost;

    if (!title || !body) {
      alert('Por favor, llena todos los campos');
      return;
    }

    const { data, error } = await supabase
      .from('prueba')  // Nombre de la tabla
      .update({ title, body })  // Actualizar el post
      .eq('id', editingPost.id);  // Filtrar por el ID del post que estamos editando

    if (error) {
      console.error('Error updating data:', error.message);
    } else {
      console.log('Data updated successfully:', data);
      setEditingPost(null);  // Limpiar el estado de edición
      setNewPost({ title: '', body: '' });  // Limpiar el formulario
      fetchData();  // Volver a cargar los datos
    }
  };

  // Usamos useEffect para ejecutar fetchData cuando el componente se monta
  useEffect(() => {
    fetchData();
  }, []);  // Solo se ejecuta una vez cuando el componente se monta

  return (
    <div className="App">
      <header className="App-header">
        <h1>Datos de la tabla "prueba"</h1>

        {/* Formulario para insertar o actualizar un post */}
        <form onSubmit={editingPost ? updatePost : insertData}>
          <input
            type="text"
            placeholder="Título del post"
            value={newPost.title}
            onChange={(e) => setNewPost({ ...newPost, title: e.target.value })}
          />
          <textarea
            placeholder="Contenido del post"
            value={newPost.body}
            onChange={(e) => setNewPost({ ...newPost, body: e.target.value })}
          />
          <button type="submit">{editingPost ? 'Actualizar Post' : 'Agregar Post'}</button>
        </form>

        {/* Mostrar los posts */}
        {loading ? (
          <p>Cargando...</p>
        ) : (
          <div>
            {data.length > 0 ? (
              <ul>
                {data.map((item) => (
                  <li key={item.id}>
                    <h2>Título: {item.title}</h2>
                    <p>Descripción: {item.body}</p>
                    <button className='update' onClick={() => editPost(item)}>Editar</button>
                  </li>
                ))}
              </ul>
            ) : (
              <p>No hay datos disponibles.</p>
            )}
          </div>
        )}
      </header>
    </div>
  );
}

export default App;
