import React, { useState } from 'react';

function Post({ title, body, postId }) {
  // Estado para manejar el "Me gusta" de cada post
  const [liked, setLiked] = useState(false);

  // Función para alternar el "Me gusta"
  const handleLike = () => {
    setLiked(!liked);
  };

  // Función para compartir
  const handleShare = () => {
    alert(`Compartiendo el post con ID: ${postId}`);
  };

  return (
    <li style={{ marginBottom: '15px' }}>
      <h2>{title}</h2>
      <p>{body}</p>
      {/* Botón de "Me gusta" */}
      <button
        onClick={handleLike}
        style={{
          border: 'none',
          background: 'none',
          cursor: 'pointer',
          color: liked ? 'red' : 'grey',
          fontSize: '18px',
          marginRight: '10px',
        }}
      >
        ❤️ {liked ? 'Me gusta' : 'Like'}
      </button>
      {/* Botón de "Compartir" */}
      <button
        onClick={handleShare}
        style={{
          border: 'none',
          background: 'none',
          cursor: 'pointer',
          color: 'blue',
          fontSize: '18px',
        }}
      >
        🔗 Compartir
      </button>
    </li>
  );
}

export default Post;
