import React, { useState, useEffect } from 'react';
import { supabase } from './SupabaseClient.ts';
import TaskForm from './TaskForm.js'; // Importa el componente TaskForm

export default function App() {
  const [tasks, setTasks] = useState([]); // Estado para las tareas

  // Función para obtener las tareas desde Supabase
  const fetchTasks = async () => {
    const { data, error } = await supabase.from('tasks').select('*').order('id', { ascending: true });
    if (!error) {
      setTasks(data);
    } else {
      console.error('Error fetching tasks:', error.message);
    }
  };

  useEffect(() => {
    fetchTasks(); // Llama a fetchTasks cuando el componente se monta
  }, []);

  return (
    <div
      style={{
        maxWidth: "600px",
        margin: "20px auto",
        fontFamily: "Arial, sans-serif",
        textAlign: "center",
        padding: "20px",
        boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
        borderRadius: "10px",
        backgroundColor: "#f9f9f9",
      }}
    >
      <h2 style={{ color: "#333" }}>Task Manager</h2>

      {/* Componente TaskForm */}
      <TaskForm onSave={fetchTasks} />

      {/* Lista de tareas */}
      <ul style={{ listStyle: "none", padding: "0" }}>
        {tasks.map((task) => (
          <li
            key={task.id}
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              marginBottom: "10px",
              padding: "10px",
              border: "1px solid #ddd",
              borderRadius: "5px",
              backgroundColor: "#fff",
            }}
          >
            <span style={{ flex: 1, textAlign: "left" }}>
              <strong>{task.title}</strong>: {task.description}
            </span>
            <div>
              {/* Botón para completar la tarea */}
              {!task.completed && (
                <button
                  onClick={async () => {
                    const { error } = await supabase
                      .from('tasks')
                      .update({ completed: true })
                      .eq('id', task.id);
                    if (!error) fetchTasks();
                  }}
                  style={{
                    padding: "5px 10px",
                    fontSize: "12px",
                    color: "white",
                    backgroundColor: "#4caf50",
                    border: "none",
                    borderRadius: "5px",
                    cursor: "pointer",
                    marginRight: "10px",
                  }}
                >
                  Complete
                </button>
              )}

              {/* Botón para eliminar la tarea */}
              <button
                onClick={async () => {
                  const { error } = await supabase
                    .from('tasks')
                    .delete()
                    .eq('id', task.id);
                  if (!error) fetchTasks();
                }}
                style={{
                  padding: "5px 10px",
                  fontSize: "12px",
                  color: "white",
                  backgroundColor: "#f44336",
                  border: "none",
                  borderRadius: "5px",
                  cursor: "pointer",
                }}
              >
                Delete
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}
