import React, { useState, useEffect } from 'react';
import { supabase } from './SupabaseClient.ts';
import TaskForm from './TaskForm.js'; // Importa el componente TaskForm
import './App.css';

export default function App() {
  const [tasks, setTasks] = useState([]); // Estado para las tareas

  // Función para obtener las tareas desde Supabase
  const fetchTasks = async () => {
    const { data, error } = await supabase.from('tasks').select('*').order('id', { ascending: true });
    if (!error) {
      setTasks(data);
    } else {
      console.error('Error fetching tasks:', error.message);
    }
  };

  const handleSubmit = async (title, description) => {
    const { error } = await supabase.from('tasks').insert([{ title, description }]);
    if (!error) fetchTasks();
  };

  const markAsCompleted = async (id) => {
    const { error } = await supabase.from('tasks').update({ completed: true }).eq('id', id);
    if (!error) fetchTasks();
  };

  const deleteTask = async (id) => {
    const { error } = await supabase.from('tasks').delete().eq('id', id);
    if (!error) fetchTasks();
  };

  useEffect(() => {
    fetchTasks();
  }, []);



  useEffect(() => {
    fetchTasks(); // Llama a fetchTasks cuando el componente se monta
  }, []);


  return (
    <div className="app-container">
      <h2>Task Manager</h2>
      <TaskForm onSave={fetchTasks} handleSubmit={handleSubmit} />
      <ul className="task-list">
        {tasks.map((task) => (
          <li key={task.id} className="task-item">
            <span className="task-text">
              <strong>{task.title}</strong>: {task.description}
            </span>
            <div>
              <button
                onClick={() => markAsCompleted(task.id)}
                className={`complete-button ${task.completed ? 'completed' : ''}`}
                disabled={task.completed}
              >
                {task.completed ? 'Completed' : 'Complete'}
              </button>
              <button onClick={() => deleteTask(task.id)} className="delete-button">
                Delete
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}
