import React, { useEffect, useState } from 'react';
import './App.css';  // Aquí puedes añadir tus estilos

// Importamos la librería de supabase
import { createClient } from '@supabase/supabase-js';

// Configuración de la conexión a Supabase
const supabaseUrl = 'https://xfoffqccaewuaeiyclpi.supabase.co';
const supabaseKey = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6Imxta2tueHJxem10d2l5dWx1amVpIiwicm9sZSI6ImFub24iLCJpYXQiOjE3MzA5Njg5OTksImV4cCI6MjA0NjU0NDk5OX0.Z8T1Cu2YVhfQFCsxfCDdJkmaI2FRp4fGVWt3tdXl9gU';

// Crear el cliente de Supabase
const supabase = createClient(supabaseUrl, supabaseKey);

function App() {
  const [data, setData] = useState([]);  // Estado para guardar los datos
  const [loading, setLoading] = useState(true);  // Estado para el indicador de carga

  // Función para obtener los datos de Supabase
  const fetchData = async () => {
    const { data, error } = await supabase
      .from('prueba')  // Nombre de la tabla
      .select('*');  // Seleccionar todos los campos

    if (error) {
      console.error('Error fetching data:', error.message);
    } else {
      setData(data);  // Guardar los datos en el estado
    }
    setLoading(false);  // Cambiar el estado de carga cuando se obtiene la respuesta
  };

  // Usamos useEffect para ejecutar fetchData cuando el componente se monta
  useEffect(() => {
    fetchData();
  }, []);  // Solo se ejecuta una vez cuando el componente se monta

  return (
    <div className="App">
      <header className="App-header">
        <h1>Datos de la tabla "prueba"</h1>

        {loading ? (
          <p>Cargando...</p>
        ) : (
          <div>
            {data.length > 0 ? (
              <ul>
                {data.map((item, index) => (
                  <li key={index}>{JSON.stringify(item)}</li>  // Muestra cada registro
                ))}
              </ul>
            ) : (
              <p>No hay datos disponibles.</p>
            )}
          </div>
        )}
      </header>
    </div>
  );
}

export default App;
