import React, { useEffect, useState } from 'react';
import './App.css';  // Aquí puedes añadir tus estilos

// Importamos la librería de supabase
import { createClient } from '@supabase/supabase-js';

// Configuración de la conexión a Supabase
const supabaseUrl = 'https://xfoffqccaewuaeiyclpi.supabase.co';
const supabaseKey = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6Inhmb2ZmcWNjYWV3dWFlaXljbHBpIiwicm9sZSI6InNlcnZpY2Vfcm9sZSIsImlhdCI6MTczMTE0ODY2OSwiZXhwIjoyMDQ2NzI0NjY5fQ.XyTOIg15jqThBhsJIWW1SlEjZBGBmqMOFK5_B005h3g';

// Crear el cliente de Supabase
const supabase = createClient(supabaseUrl, supabaseKey);

function App() {
  const [data, setData] = useState([]);  // Estado para guardar los datos
  const [loading, setLoading] = useState(true);  // Estado para el indicador de carga
  const [newPost, setNewPost] = useState({ title: '', body: '' });  // Estado para el formulario

  // Función para obtener los datos de Supabase
  const fetchData = async () => {
    const { data, error } = await supabase
      .from('prueba')  // Nombre de la tabla
      .select('*');  // Seleccionar todos los campos

    if (error) {
      console.error('Error fetching data:', error.message);
    } else {
      setData(data);  // Guardar los datos en el estado
    }
    setLoading(false);  // Cambiar el estado de carga cuando se obtiene la respuesta
  };

  // Función para insertar un nuevo post
  const insertData = async (e) => {
    e.preventDefault(); // Evitar que la página se recargue
    const { title, body } = newPost;

    if (!title || !body) {
      alert('Por favor, llena todos los campos');
      return;
    }

    const { data, error } = await supabase
      .from('prueba')  // Nombre de la tabla
      .insert([
        { title, body },
      ]);

    if (error) {
      console.error('Error inserting data:', error.message);
    } else {
      console.log('Data inserted successfully:', data);
      setNewPost({ title: '', body: '' });  // Limpiar el formulario
      fetchData();  // Volver a cargar los datos
    }
  };

  // Usamos useEffect para ejecutar fetchData cuando el componente se monta
  useEffect(() => {
    fetchData();
  }, []);  // Solo se ejecuta una vez cuando el componente se monta

  return (
    <div className="App">
      <header className="App-header">
        <h1>Datos de la tabla "prueba"</h1>

        {/* Formulario para insertar un nuevo post */}
        <form onSubmit={insertData}>
          <input
            type="text"
            placeholder="Título del post"
            value={newPost.title}
            onChange={(e) => setNewPost({ ...newPost, title: e.target.value })}
          />
          <textarea
            placeholder="Contenido del post"
            value={newPost.body}
            onChange={(e) => setNewPost({ ...newPost, body: e.target.value })}
          />
          <button type="submit">Agregar Post</button>
        </form>

        {/* Mostrar los posts */}
        {loading ? (
          <p>Cargando...</p>
        ) : (
          <div>
            {data.length > 0 ? (
              <ul>
                {data.map((item) => (
                  <li key={item.id}>
                    <h2>Título: {item.title}</h2>
                    <p>Descripción: {item.body}</p>
                  </li>
                ))}
              </ul>
            ) : (
              <p>No hay datos disponibles.</p>
            )}
          </div>
        )}
      </header>
    </div>
  );
}

export default App;
