import React, { useEffect, useState } from 'react';
import './App.css';
import { createClient } from '@supabase/supabase-js';

// Configura el cliente de Supabase
const supabaseUrl = 'https://lmkknxrqzmtwiyulujei.supabase.co';
const supabaseAnonKey = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6Imxta2tueHJxem10d2l5dWx1amVpIiwicm9sZSI6ImFub24iLCJpYXQiOjE3MzA5Njg5OTksImV4cCI6MjA0NjU0NDk5OX0.Z8T1Cu2YVhfQFCsxfCDdJkmaI2FRp4fGVWt3tdXl9gU';
const supabase = createClient(supabaseUrl, supabaseAnonKey);

function App() {
  // Estado para almacenar las publicaciones
  const [posts, setPosts] = useState([]);

  // Llamada a la API al cargar el componente
  useEffect(() => {
    // Función asíncrona para obtener los datos
    const fetchPosts = async () => {
      const { data, error } = await supabase
        .from('prueba0')
        .select();

      if (error) {
        console.error('Error fetching data:', error);
      } else {
        setPosts(data); // Actualiza el estado con los datos obtenidos
      }
    };

    fetchPosts(); // Llama a la función para obtener los datos
  }, []);

  return (
    <div className="App">
      <h1>Posts</h1>
      <ul>
        {posts.map((post) => (
          <li key={post.id}>{post.nombre_campo}</li> // Muestra el campo 'nombre_campo' de cada publicación
        ))}
      </ul>
    </div>
  );
}

export default App;
